import React, { useState } from 'react';
import Flashcard from '/Users/yerimsong/Documents/Spring2024/harbor/src/components/flashcard.js';

const flashcardsData = [
  { front: 'Front of Card 1', back: 'Back of Card 1' },
  { front: 'Front of Card 2', back: 'Back of Card 2' },
  { front: 'Front of Card 3', back: 'Back of Card 3' },
  // ...add as many flashcards as you like
];

const Page = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) => prevIndex > 0 ? prevIndex - 1 : flashcardsData.length - 1);
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) => prevIndex < flashcardsData.length - 1 ? prevIndex + 1 : 0);
  };

  return (
    // <div className="flex justify-between p-4">
    //         <div className="flex-1 mr-2 p-4 border-r-2">
    //             <h1 className="text-2xl font-bold mb-4">Solve</h1>
    //             <p>Harbor is a project to translate text validated by humans</p>
    //         </div>
    //         <div className="flex-1 ml-2 p-4">
    //             <h1 className="text-2xl font-bold mb-4">Publish</h1>
    //             <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua...</p>
    //         </div>
    //     </div>

    <div className="flex justify-between p-4">
        <div className="flex-1 mr-2 p-4 border-r-2">
            <h1 className="text-2xl font-bold mb-4">Solve</h1>
            <p>Harbor is a project to translate text validated by humans</p>
        </div>
      <div className="bg-gray-400 shadow mb-5">
        Solver instructions here
      </div>
      <Flashcard
        frontContent={<div className="text-center">{flashcardsData[currentIndex].front}</div>}
        backContent={<div className="text-center">{flashcardsData[currentIndex].back}</div>}
      />
      <div className="flex mt-5">
        <button className="mx-2 bg-gray-300 hover:bg-gray-400 text-black font-bold py-2 px-4 rounded" onClick={goToPrevious}>
          Previous
        </button>
        <button className="mx-2 bg-gray-300 hover:bg-gray-400 text-black font-bold py-2 px-4 rounded" onClick={goToNext}>
          Next
        </button>
      </div>
    </div>
  );
};

export default Page;
